import 'package:flutter/material.dart';

// drop_down.dart
class DropDownWidget extends StatefulWidget {
  @override
  State<StatefulWidget> createState() {
    return _DropDownState();
  }
}

class _DropDownState extends State<DropDownWidget> {
  var _selectItemValue;
  List<DropdownMenuItem> itemList() {
    List<DropdownMenuItem> items = new List();
    items.add(new DropdownMenuItem(value: '赵一', child: new Text('赵一')));
    items.add(new DropdownMenuItem(value: '钱二', child: new Text('钱二')));
    items.add(new DropdownMenuItem(value: '孙三', child: new Text('孙三')));
    return items;
  }

  @override
  Widget build(BuildContext context) {
    // 下拉菜单展示的内容处没有下划线
    return new DropdownButtonHideUnderline(
      child: new DropdownButton(
        hint: new Text('下拉菜单选择一个人名'),
        value: _selectItemValue,
        items: itemList(),
        onChanged: (T) {
          setState(() {
            _selectItemValue = T;
          });
        },
      ),
    );
  }
}
